mapper.update:
update student
username = #{username,jdbcType=VARCHAR}, `password` = #{password,jdbcType=VARCHAR}, real_name = #{realName,jdbcType=VARCHAR}, school_id = #{schoolId,jdbcType=VARCHAR}, team_ids = #{teamIds,jdbcType=VARCHAR}, tel = #{tel,jdbcType=VARCHAR}, avatar_path = #{avatarPath,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select password
from student
where username = #{username}

select team_ids
from student
where username = #{username}

select username
from student
where id = #{id}

select real_name
from student
where id = #{id}
