mapper.update:
update teacher
username = #{username,jdbcType=VARCHAR}, `password` = #{password,jdbcType=VARCHAR}, real_name = #{realName,jdbcType=VARCHAR}, team_ids = #{teamIds,jdbcType=VARCHAR}, school_id = #{schoolId,jdbcType=INTEGER}, tel = #{tel,jdbcType=VARCHAR}, avatar_path = #{avatarPath,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select password
from teacher
where username = #{username}

select team_ids
from teacher
where id = #{id}

select team_ids
from teacher
where username = #{username}
