mapper.update:
update team
`name` = #{name,jdbcType=VARCHAR}, member_ids = #{memberIds,jdbcType=VARCHAR}, project_id = #{projectId,jdbcType=INTEGER}, teacher_id = #{teacherId,jdbcType=INTEGER},
where id = #{id,jdbcType=INTEGER}

mapper.select:
select member_ids
from team
where id = #{id}

select teacher_id
from team
where id ={id}
